import React, { useContext, useEffect } from 'react';
import noteContext from '../context/notes/NoteContext';
import NoteItem from './NoteItem';

const Notes = () => {
    const context = useContext(noteContext);
    const { notes, getNote,deleteNote } = context;

    useEffect(() => {
        getNote();
    }, [getNote]);

    return (
        <>
         
            <div className="row my-3">
                <h1>Your Notes</h1>
                {notes.map((note) => {
                    return  <div className="col-md-4 my-2" key={note._id}>
      
                    <div className="card" >
                 
                 <div className="card-body">
                   <div className="d-flex align-items-center"><h5 className="card-title">{note.title}</h5>
                   <i className="fa-solid fa-delete-left mx-3" onClick={()=>{ deleteNote(note._id)}}></i>
                   <i className="fa-solid fa-pen-to-square mx-3" ></i>
                   </div>
                   
                   <p className="card-text"> {note.description}</p>
                 
                 </div>
               </div>
                   </div>
                })}
            </div>
        </>
    );
};

export default Notes;
