<div className="unique-background">
<div className="unique-shape"></div>
<div className="unique-shape"></div>
<form className="unique-form">
  <h3 className="unique-form-title">Login Here</h3>

  <label htmlFor="unique-username">Username</label>
  <input
    type="text"
    id="unique-username"
    name="username"
    className="form-control"
    value={credential.username}
    onChange={onChange}
  />

  <label htmlFor="unique-password">Password</label>
  <input
