import React, { useState } from 'react';
import {useHistory} from 'react';

const Signup = () => {

  const [credential,setCredential] = useState({name:"",email:"",password:"",cpassword:""})
  let history = useHistory()

  const handleClick=async(e)=>{
    e.preventDefault();
    const {name,email,password,cpassword} = credential;
    const host = "http://localhost:5000";
    const response = await fetch(`${host}/api/auth/createuser`, {

        method: 'POST',
        headers: {
          'Content-Type': 'application/json',

        },
        body: JSON.stringify({name,email,password })
      });
      const json = await response.json();
    if(json.success){
        //redirect
        localStorage.setItem('token',json.authtoken)
        history.pushState("/")

    }else{
        alert("invalid credential")
    }


  }
  const onchange = (e) =>{
    setCredential({ ...credential, [e.target.name]: e.target.value });
  }
  return (
    <div className="container my-2 mx-2">
  <div className="mb-3">
  <label htmlFor="exampleFormControlInput1" className="form-label">Name</label>
  <input value={onchange} name="name" type="text" className="form-control" id="exampleFormControlInput1" placeholder="name"/>
</div>
<div className="mb-3">
  <label htmlFor="exampleFormControlInput1" className="form-label">Email address</label>
  <input value={onchange} type="email" name="email" className="form-control" id="exampleFormControlInput1" placeholder="name@example.com"/>
</div>

<label htmlFor="inputPassword5" className="form-label">Password</label>
<input value={onchange} type="password" name="password" id="inputPassword5" className="form-control" aria-describedby="passwordHelpBlock"/>
<div id="passwordHelpBlock" className="form-text">
  Your password must be 8-20 characters long, contain letters and numbers, and must not contain spaces, special characters, or emoji.
</div>
<label htmlFor="inputPassword5" className="form-label">Confirm your password</label>
<input value={onchange}  type="password" id="inputPassword5" name="cname" className="form-control" aria-describedby="passwordHelpBlock"/>
<div id="passwordHelpBlock" className="form-text">
  Your password must be 8-20 characters long, contain letters and numbers, and must not contain spaces, special characters, or emoji.
</div>
<button type="button" className="btn btn-info" onClick={handleClick}>Submit</button>
      
    </div>
  )
}

export default Signup
