import React, { useState, useEffect, useContext } from 'react';
import { useHistory } from 'react-router-dom';
import noteContext from '../context/notes/NoteContext';
import "../login.css";

const Login = (props) => {
    const [credential, setCredential] = useState({ email: "", password: "" });
    const history = useHistory();
    const context = useContext(noteContext);
    const { setIsAuthenticated } = context;

      useEffect(()=>{
        if(setIsAuthenticated){
          history.push('/')
        }
      },[])
    const handleSubmit = async (e) => {
        e.preventDefault();
        const host = "http://localhost:5000";
        const response = await fetch(`${host}/api/auth/login`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email: credential.email, password: credential.password })
        });
        const json = await response.json();
        if (json.success) {
            // redirect
            localStorage.setItem('token', json.authtoken);
            props.onLoginSuccess();
            setIsAuthenticated(true);
            history.push("/");
        } else {
            alert("Invalid credentials");
        }
    };

    const onChange = (e) => {
        setCredential({ ...credential, [e.target.name]: e.target.value });
    };

    const handleSignupRedirect = () => {
        history.push('/signup');
    };

    return (
        <div className="container my-3">
            <div className="mb-2">
                <div className="unique-background mb-2">
                    <div className="unique-shape one"></div>
                    <div className="unique-shape two"></div>
                    <form className="unique-form mx-2 my-3" >
                        <h3 className="unique-form-title">Login Here</h3>
                        <label htmlFor="unique-email">Email</label>
                        <input
                            type="email"
                            id="unique-email"
                            name="email"
                            className="form-control"
                            value={credential.email}
                            onChange={onChange}
                            autoComplete="email"
                        />
                        <label htmlFor="unique-password">Password</label>
                        <input
                            type="password"
                            id="unique-password"
                            name="password"
                            className="form-control"
                            value={credential.password}
                            onChange={onChange}
                            autoComplete="current-password"
                        />
                        <div className="btn-container md-2 my-3">
                            <button type="submit" className="unique-btn" onClick={handleSubmit}>Log In</button>
                            <button type="button" className="unique-btn" onClick={handleSignupRedirect}>Sign Up</button>
                        </div>
                        <div className="unique-social">
                            <div className="unique-go">
                                <i className="fab fa-google"></i> Google
                            </div>
                            <div className="unique-fb">
                                <i className="fab fa-facebook"></i> Facebook
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
