import React, { useState ,useEffect,useContext} from 'react';
import { useHistory } from 'react-router-dom';
import noteContext from '../context/notes/NoteContext';


const Login = (props) => {
    const [credential,setCredential] = useState({email:"",password:""})
    const history = useHistory();
    const context = useContext(noteContext);
   

    useEffect(() => {
      const checkToken = async () => {
          try {
              const token = localStorage.getItem('token');
              if (token) {
                  const host = "http://localhost:5000";
                  const response = await fetch(`${host}/api/auth/getuser`, {
                      method: 'POST',
                      headers: {
                          'Content-Type': 'application/json',
                          'Auth': token
                      },
                  });
                  const json = await response.json();
                  if (json) {
                      // If token exists, redirect to home page
                      history.push("/");
                  }
              }
          } catch (error) {
              console.error('Error checking token:', error);
              // Handle error if needed
          }
      };
  
      checkToken();
  }, []);
    const handleClick = async (e) =>{
        e.preventDefault();
        const host = "http://localhost:5000";
        const response = await fetch(`${host}/api/auth/login`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',

            },
            body: JSON.stringify({email:credential.email,password:credential.password })
          });
          const json = await response.json();
        if(json.success){
            //redirect
            localStorage.setItem('token',json.authtoken)
            props.onLoginSuccess();
            history.push("/")
            


        }else{
            alert("invalid credential")
        }



    }
    const onChange = (e) => {
        setCredential({ ...credential, [e.target.name]: e.target.value });
    };
  return (
    <div className="container mx-2 my-2">
     <div className="mb-3">
  <label htmlFor="exampleFormControlInput1" className="form-label">Email address</label>
  <input type="email" className="form-control" id="exampleFormControlInput1" name="email" value={credential.email} placeholder="name@example.com" onChange={onChange}/>

</div>
<label htmlFor="inputPassword5" className="form-label">Password</label>
<input type="password" id="inputPassword5"  name="password" className="form-control" value={credential.password} aria-describedby="passwordHelpBlock" onChange={onChange}/>
<div id="passwordHelpBlock" className="form-text">
  Your password must be 8-20 characters long, contain letters and numbers, and must not contain spaces, special characters, or emoji.
</div>
<button type="button" className="btn btn-info" onClick={handleClick}>Submit</button>
    </div>
  )
}

export default Login
