import React from "react";

import NoteContext from "./NoteContext";

const NoteState = (props)=>{

    const state = {
        "name":"Jit",
        "class":"5b"
    }
    return (
        <NoteContext.Provider value={state}>
            {props.children}
        </NoteContext.Provider>
    )
}

export default NoteState;
