import React,{ useContext, useState } from 'react'
import noteContext from '../context/notes/NoteContext';




export const Addnote = () => {


    const context = useContext(noteContext);
    const{addNote}=context;
    const [note,setNote] = useState({title:"",description:"",tag:"default"})
const handleclick=(e)=>{
    e.preventDefault()
    addNote(note.title,note.description,note.tag);

}
const onCHange=(e)=>{
    setNote({...note,[e.target.name]:e.target.value})
  
}


  return (
    
        <div className="container my-3">
     <h1>Add a Note</h1>
     <div className="mb-3">
  <label htmlFor="title" className="form-label">Title</label>
  <input type="text" className="form-control" id="title" placeholder="name@example.com" onChange={onCHange} name="title" />
</div>
<div className="mb-3">
  <label htmlFor="Tag" className="form-label">Tag</label>
  <input type="text" className="form-control" id="Tag" placeholder="name@example.com" onChange={onCHange}  name="tag"/>
</div>
<div className="mb-3">
  <label htmlFor="Description" className="form-label">Description</label>
  <textarea className="form-control" id="Description" rows="3" onChange={onCHange} name="description" ></textarea>
</div>
 
 <button type="submit" className="btn btn-primary" onClick={handleclick}>add a note</button>
</div>
   
  )
}
