import React, { useState, useEffect } from "react";
import NoteContext from "./NoteContext";

const NoteState = (props) => {
  const host = "http://localhost:5000";
  const [notes, setNotes] = useState([]);

  // Get notes
  const getNote = async () => {
    const response = await fetch(`${host}/api/notes/fetchallnotes`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Auth': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjY0OGI4MThjZGQxZTViODk2ZGE2OTUwIn0sImlhdCI6MTcxNjA0Nzk0MH0.POTBGaZlIG4FxHU2XHtxXaeRg5Az3LZpdN5XupHUArE'
      }
    });
    const json = await response.json();
    setNotes(json);
  };

  // Add a note
  const addNote = async (title, description, tag) => {
    const response = await fetch(`${host}/api/notes/addnote`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Auth': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjY0OGI4MThjZGQxZTViODk2ZGE2OTUwIn0sImlhdCI6MTcxNjA0Nzk0MH0.POTBGaZlIG4FxHU2XHtxXaeRg5Az3LZpdN5XupHUArE'
      },
      body: JSON.stringify({ title, description, tag })
    });
    const json = await response.json();
    setNotes((prevNotes) => [...prevNotes, json]);
  };

  // Delete a note
  const deleteNote = async (id) => {
    // Add API call to delete the note from the server
    const response = await fetch(`${host}/api/notes/deletenote/${id}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
        'Auth': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjY0OGI4MThjZGQxZTViODk2ZGE2OTUwIn0sImlhdCI6MTcxNjA0Nzk0MH0.POTBGaZlIG4FxHU2XHtxXaeRg5Az3LZpdN5XupHUArE'
      }
    });
    const json = response.json();

  };

  // Edit a note
  const editNote = async (id, title, description, tag) => {
    const response = await fetch(`${host}/api/notes/updateNotes/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Auth': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjY0OGI4MThjZGQxZTViODk2ZGE2OTUwIn0sImlhdCI6MTcxNjA0Nzk0MH0.POTBGaZlIG4FxHU2XHtxXaeRg5Az3LZpdN5XupHUArE'
      },
      body: JSON.stringify({ title, description, tag })
    });
    const json = await response.json();

    // Update the note in the local state
    const newNotes = notes.map((note) => (note._id === id ? json : note));
    setNotes(newNotes);
  };

  useEffect(() => {
    getNote();
  }, []);

  return (
    <NoteContext.Provider value={{ notes, addNote, deleteNote, editNote, getNote }}>
      {props.children}
    </NoteContext.Provider>
  );
};

export default NoteState;
