import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  const [credential, setCredential] = useState({ name: "", email: "", password: "", cpassword: "" });
  const [error, setError] = useState("");
  let navigate = useNavigate();

  const handleClick = async (e) => {
    e.preventDefault();
    const { name, email, password, cpassword } = credential;
    
    if (password !== cpassword) {
      setError("Passwords do not match");
      return;
    }
    
    if (!name || !email || !password || !cpassword) {
      setError("All fields are required");
      return;
    }
    
    const host = "http://localhost:5000";
    const response = await fetch(`${host}/api/auth/createuser`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name, email, password })
    });
    const json = await response.json();
    if (json.success) {
      localStorage.setItem('token', json.authtoken);
      navigate("/");
    } else {
      setError("Invalid credentials or user already exists");
    }
  };

  const onChange = (e) => {
    setCredential({ ...credential, [e.target.name]: e.target.value });
  };

  return (
    <div className="container my-2 mx-2">
      {error && <div className="alert alert-danger">{error}</div>}
      <div className="mb-3">
        <label htmlFor="name" className="form-label">Name</label>
        <input value={credential.name} onChange={onChange} name="name" type="text" className="form-control" id="name" placeholder="Name" />
      </div>
      <div className="mb-3">
        <label htmlFor="email" className="form-label">Email address</label>
        <input value={credential.email} onChange={onChange} type="email" name="email" className="form-control" id="email" placeholder="name@example.com" />
      </div>
      <div className="mb-3">
        <label htmlFor="password" className="form-label">Password</label>
        <input value={credential.password} onChange={onChange} type="password" name="password" id="password" className="form-control" aria-describedby="passwordHelpBlock" />
        <div id="passwordHelpBlock" className="form-text">
          Your password must be 8-20 characters long, contain letters and numbers, and must not contain spaces, special characters, or emoji.
        </div>
      </div>
      <div className="mb-3">
        <label htmlFor="cpassword" className="form-label">Confirm your password</label>
        <input value={credential.cpassword} onChange={onChange} type="password" name="cpassword" id="cpassword" className="form-control" aria-describedby="passwordHelpBlock" />
      </div>
      <button type="button" className="btn btn-info" onClick={handleClick}>Submit</button>
    </div>
  );
}

export default Signup;
